import React from 'react';
import Carousel from 'react-bootstrap/Carousel';
import { useNavigate } from "react-router-dom";
import '/workspaces/project/src/css/Carrusel.css';

const feather = require('feather-icons');

setTimeout(() => {
  feather.replace();
}, 1000);

const Carrusel = () => {
  const navigate = useNavigate();
  const [activeIndex, setActiveIndex] = useState(0);

  const handleClick = (slug) => {
    navigate("/detalle/" + slug);
  }

  const handleSelect = (selectedIndex, e) => {
    setActiveIndex(selectedIndex);
  };

  const itemsPerPage = 3;
  const totalItems = playas.length;

  return (
    <div className="carrusel-container">
      <Carousel activeIndex={activeIndex} onSelect={handleSelect} interval={null} wrap={true}>
        {[...Array(Math.ceil(totalItems / itemsPerPage))].map((_, index) => (
          <Carousel.Item key={index}>
            <div className="row">
              {playas.slice(index * itemsPerPage, index * itemsPerPage + itemsPerPage).map((item, subIndex) => (
                <div key={index * itemsPerPage + subIndex} className="col">
                  <img
                    className='w-100 h-100 dark-image'
                    src={item.picture}
                    alt={`Playa ${item.slug}`}
                    onClick={() => handleClick(item.slug)}
                  />
                </div>
              ))}
            </div>
          </Carousel.Item>
        ))}
      </Carousel>
    </div>
  );
}

export default Carrusel;

const playas = [
  {
    slug: 'playa-1',
    picture: 'https://static.vecteezy.com/system/resources/thumbnails/035/095/662/small_2x/ai-generated-copy-space-tropical-beach-landscape-ai-generative-photo.jpg'
  },
  {
    slug: 'playa-2',
    picture: 'https://i.pinimg.com/474x/17/f0/13/17f013312dfa35c8e242602bb7835ecc.jpg'
  },
  {
    slug: 'playa-3',
    picture: 'https://static.vecteezy.com/system/resources/thumbnails/038/252/827/small_2x/ai-generated-sunny-tropical-beach-sea-shells-photo.jpg'
  },
  {
    slug: 'playa-4',
    picture: 'https://static.vecteezy.com/system/resources/thumbnails/038/252/827/small_2x/ai-generated-sunny-tropical-beach-sea-shells-photo.jpg'
  },
];
