import React from 'react';
import Carousel from 'react-bootstrap/Carousel';
import { useNavigate } from "react-router-dom";
import '/workspaces/project/src/css/Carrusel.css';

const feather = require('feather-icons');

const Carrusel = () => {
  const navigate = useNavigate();
  const [playas, setPlayas] = useState([]);

  useEffect(() => {
    feather.replace();
    // Simulando carga de datos desde el archivo JSON
    // En un entorno real, puedes usar fetch o axios para cargar datos desde el archivo JSON
    setPlayas(playasData);
  }, []);

  const handleClick = (slug) => {
    navigate(`/detalle/${slug}`);
  }

  return (
    <div className="carrusel-container">
      <Carousel interval={null} indicators={false} keyboard wrap>
        {[...playas, ...playas].map((item, index) => (
          index % 3 === 0 ? (
            <Carousel.Item key={index}>
              <div className="row">
                {playas.slice(index, index + 3).map((playa, subIndex) => (
                  <div key={index + subIndex} className="col">
                    <img
                      className='w-100 h-100 dark-image'
                      src={playa.picture}
                      alt={`Playa ${playa.slug}`}
                      onClick={() => handleClick(playa.slug)}
                    />
                  </div>
                ))}
              </div>
            </Carousel.Item>
          ) : null
        ))}
      </Carousel>
    </div>
  );
}

export default Carrusel;
