import React, { useState } from 'react';
import Carousel from 'react-bootstrap/Carousel';
import { useNavigate } from 'react-router-dom';
import '/workspaces/project/src/css/Carrusel.css'; // Ajusta la ruta según tu estructura de archivos

const feather = require('feather-icons');

setTimeout(() => {
  feather.replace();
}, 1000);

const Carrusel = ({ playas }) => {
  const navigate = useNavigate();
  const [index, setIndex] = useState(0);
  const itemsPerPage = 3;

  const handleClick = (slug) => {
    navigate(`/detalle/${slug}`);
  };

  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
  };

  const renderCarouselItems = () => {
    if (!playas || playas.length === 0) {
      return <p>No hay playas para mostrar.</p>;
    }

    const pages = Math.ceil(playas.length / itemsPerPage);
    const carouselItems = [];

    for (let i = 0; i < pages; i++) {
      const startIndex = i * itemsPerPage;
      const slicedPlayas = playas.slice(startIndex, startIndex + itemsPerPage);

      carouselItems.push(
        <Carousel.Item key={i}>
          <div className="row">
            {slicedPlayas.map((item, subIndex) => (
              <div key={startIndex + subIndex} className="col">
                <img
                  className="w-100 h-100 dark-image"
                  src={item.picture}
                  alt={`Playa ${item.slug}`}
                  onClick={(e) => {
                    e.preventDefault();
                    handleClick(item.slug);
                  }}
                />
              </div>
            ))}
          </div>
        </Carousel.Item>
      );
    }

    return carouselItems;
  };

  return (
    <div className="carrusel-container">
      <Carousel
        activeIndex={index}
        onSelect={handleSelect}
        interval={null}
        wrap={true} // Permite que el carrusel se enrolle
        prevIcon={<span className="carousel-control-prev-icon" />}
        nextIcon={<span className="carousel-control-next-icon" />}
      >
        {renderCarouselItems()}
      </Carousel>
    </div>
  );
};

export default Carrusel;
