import React, { useState } from 'react';
import Carousel from 'react-bootstrap/Carousel';
import { useNavigate } from 'react-router-dom';
import '/workspaces/project/src/css/Carrusel.css'; // Ajusta la ruta según tu estructura de archivos

const feather = require('feather-icons');

setTimeout(() => {
  feather.replace();
}, 1000);

const Carrusel = ({ playas }) => {
  const navigate = useNavigate();
  const [index, setIndex] = useState(0);
  const itemsPerPage = 3;

  const handleClick = (slug) => {
    navigate(`/detalle/${slug}`);
  };

  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
  };

  const renderCarouselItems = () => {
    if (!playas || playas.length === 0) {
      return <p>No hay playas para mostrar.</p>;
    }

    const carouselItems = [];

    // Calcular el índice inicial basado en el estado actual 'index'
    const startIndex = index * itemsPerPage;
    const slicedPlayas = playas.slice(startIndex, startIndex + itemsPerPage);

    // Renderizar los elementos del carrusel
    carouselItems.push(
      <Carousel.Item key={index}>
        <div className="row">
          {slicedPlayas.map((item) => (
            <div key={item.slug} className="col">
              <img
                className="w-100 h-100 dark-image"
                src={item.picture}
                alt={`Playa ${item.slug}`}
                onClick={(e) => {
                  e.preventDefault();
                  handleClick(item.slug);
                }}
              />
            </div>
          ))}
        </div>
      </Carousel.Item>
    );

    return carouselItems;
  };

  return (
    <div className="carrusel-container">
      <Carousel
        activeIndex={index}
        onSelect={handleSelect}
        interval={null}
        wrap={true} // Permitir que el carrusel se reinicie al llegar al final
        prevIcon={<span className="carousel-control-prev-icon" />}
        nextIcon={<span className="carousel-control-next-icon" />}
      >
        {renderCarouselItems()}
      </Carousel>
    </div>
  );
};

export default Carrusel;
